  Parse.Cloud.define('aFunction', () => {
    throw 'it failed!';
  });
  Parse.Cloud.run('aFunction', {
    foo: 'bar'
  }).catch(() => {}).then(() => {
    const logs = spy.calls.all().reverse();
    expect(logs[0].args[1]).toBe('Parse error: ');
    expect(logs[0].args[2].message).toBe('it failed!');
    const log = logs[1].args;
    expect(log[0]).toEqual('error');
    expect(log[1]).toMatch(/Failed running cloud function aFunction for user [^ ]* with:\n {2}Input: {"foo":"bar"}\n {2}Error:/);
    const errorString = JSON.stringify(new Parse.Error(Parse.Error.SCRIPT_FAILED, 'it failed!'));
    expect(log[1].indexOf(errorString)).toBeGreaterThan(0);
    done();
