it('does scrub simple passwords on GET login', done => {
  reconfigureServer({
    verbose: true
  }).then(function () {
    request({
      headers: headers,
      url: 'http://localhost:8378/1/login?username=test&password=simplepass.com'
    }).catch(() => {}).then(() => {
      request({
        url: 'http://localhost:8378/1/scriptlog?size=4&level=verbose',
        headers: headers
      }).then(response => {
