    url: 'http://localhost:8378/1/classes/MyObject/' + myObject.id,
    body: {
      myAttribute: 'myValue'
    }
  });
  let found = false;
  Collection.prototype.findOneAndUpdate.calls.all().forEach(call => {
    found = true;
    expect(call.args[2].session).toBeFalsy();
  });
  expect(found).toBe(true);
});

