it('allows file upload only for authenticated user by default', async () => {
  await reconfigureServer({
    fileUpload: {}
  });
  let file = new Parse.File('hello.txt', data, 'text/plain');
  await expectAsync(file.save()).toBeRejectedWith(new Parse.Error(Parse.Error.FILE_SAVE_ERROR, 'File upload by public is disabled.'));
  file = new Parse.File('hello.txt', data, 'text/plain');
  const anonUser = await Parse.AnonymousUtils.logIn();
  await expectAsync(file.save({
    sessionToken: anonUser.getSessionToken()
  })).toBeRejectedWith(new Parse.Error(Parse.Error.FILE_SAVE_ERROR, 'File upload by anonymous user is disabled.'));
  file = new Parse.File('hello.txt', data, 'text/plain');
  const authUser = await Parse.User.signUp('user', 'password');
  await expectAsync(file.save({
    sessionToken: authUser.getSessionToken()
  })).toBeResolved();
});

