  });
  await expectAsync(apolloClient.query({
    query: gql`
        query GetSuperCar($id: ID!) {
          superCar(id: $id) {
            id
          }
        }
      `,
    variables: {
      id: superCar.id
    }
  })).toBeResolved();
  await expectAsync(apolloClient.query({
    query: gql`
        query FindCustomer {
          customers {
            count
          }
        }
      `
  })).toBeResolved();
  const graphQLConfig = {
    classConfigs: [{
      className: 'SuperCar',
      query: {
        get: false,
        find: true
      }
    }, {
      className: 'Customer',
      query: {
        get: true,
        find: false
      }
    }]
  };
  await parseGraphQLServer.setGraphQLConfig(graphQLConfig);
  await resetGraphQLCache();
  await expectAsync(apolloClient.query({
    query: gql`
        query GetSuperCar($id: ID!) {
          superCar(id: $id) {
            id
          }
        }
      `,
    variables: {
      id: superCar.id
    }
  })).toBeRejected();
  await expectAsync(apolloClient.query({
    query: gql`
        query GetCustomer($id: ID!) {
          customer(id: $id) {
            id
          }
        }
      `,
    variables: {
      id: customer.id
    }
  })).toBeResolved();
  await expectAsync(apolloClient.query({
    query: gql`
        query FindSuperCar {
          superCars {
            count
          }
        }
      `
  })).toBeResolved();
  await expectAsync(apolloClient.query({
    query: gql`
        query FindCustomer {
          customers {
            count
          }
        }
      `
  })).toBeRejected();
});

