        pointer: {
          __type: 'Pointer',
          className: 'SecondaryObject',
          objectId: getSecondaryObjectsResult.data.secondaryObject4.objectId
        },
        secondaryObject2: getSecondaryObjectsResult.data.secondaryObject2.id,
        secondaryObject4: getSecondaryObjectsResult.data.secondaryObject4.objectId
      },
      context: {
        headers: {
          'X-Parse-Master-Key': 'test'
        }
      }
    });
    const updatePrimaryObjectResult = await apolloClient.mutate({
      mutation: gql`
        mutation UpdatePrimaryObject(
          $id: ID!
          $secondaryObject2: ID!
          $secondaryObject4: ID!
        ) {
          updatePrimaryObject(
            input: {
              id: $id
              fields: {
                pointerField: { link: $secondaryObject4 }
                relationField: { remove: [$secondaryObject2, $secondaryObject4] }
              }
            }
          ) {
            primaryObject {
              id
              stringField
              arrayField {
                ... on Element {
                  value
                }
                ... on SecondaryObject {
                  someField
                }
              }
              pointerField {
                id
                objectId
                someField
              }
              relationField {
                edges {
                  node {
                    id
                    objectId
                    someField
                  }
                }
              }
            }
          }
        }
      `,
      variables: {
        id: createPrimaryObjectResult.data.createPrimaryObject.primaryObject.id,
        secondaryObject2: getSecondaryObjectsResult.data.secondaryObject2.id,
        secondaryObject4: getSecondaryObjectsResult.data.secondaryObject4.objectId
      },
      context: {
        headers: {
          'X-Parse-Master-Key': 'test'
        }
      }
    });
    expect(createPrimaryObjectResult.data.createPrimaryObject.primaryObject.stringField).toEqual('some value');
    expect(createPrimaryObjectResult.data.createPrimaryObject.primaryObject.arrayField).toEqual([{
      __typename: 'Element',
      value: 1
    }, {
      __typename: 'Element',
      value: 'abc'
    }, {
      __typename: 'SecondaryObject',
      someField: 'some value 44'
    }]);
    expect(createPrimaryObjectResult.data.createPrimaryObject.primaryObject.pointerField.someField).toEqual('some value 22');
    expect(createPrimaryObjectResult.data.createPrimaryObject.primaryObject.relationField.edges.map(value => value.node.someField).sort()).toEqual(['some value 22', 'some value 44']);
    expect(updatePrimaryObjectResult.data.updatePrimaryObject.primaryObject.stringField).toEqual('some value');
    expect(updatePrimaryObjectResult.data.updatePrimaryObject.primaryObject.arrayField).toEqual([{
      __typename: 'Element',
      value: 1
    }, {
      __typename: 'Element',
      value: 'abc'
    }, {
      __typename: 'SecondaryObject',
      someField: 'some value 44'
    }]);
    expect(updatePrimaryObjectResult.data.updatePrimaryObject.primaryObject.pointerField.someField).toEqual('some value 44');
    expect(updatePrimaryObjectResult.data.updatePrimaryObject.primaryObject.relationField.edges).toEqual([]);
  } catch (e) {
    handleError(e);
  }
});

