it('should return many child objects in allow cyclic query', async () => {
  const obj1 = new Parse.Object('Employee');
  const obj2 = new Parse.Object('Team');
  const obj3 = new Parse.Object('Company');
  const obj4 = new Parse.Object('Country');
  obj1.set('name', 'imAnEmployee');
  await obj1.save();
  obj2.set('name', 'imATeam');
  obj2.set('employees', [obj1]);
  await obj2.save();
  obj3.set('name', 'imACompany');
  obj3.set('teams', [obj2]);
  obj3.set('employees', [obj1]);
  await obj3.save();
  obj4.set('name', 'imACountry');
  obj4.set('companies', [obj3]);
  await obj4.save();
  obj1.set('country', obj4);
  await obj1.save();
  await parseGraphQLServer.parseGraphQLSchema.schemaCache.clear();
  const result = (await apolloClient.query({
    query: gql`
        query DeepComplexGraphQLQuery($id: ID!) {
          country(id: $id) {
            objectId
            name
            companies {
              ... on Company {
                objectId
                name
                employees {
                  ... on Employee {
                    objectId
                    name
                  }
                }
                teams {
                  ... on Team {
                    objectId
                    name
                    employees {
                      ... on Employee {
                        objectId
                        name
                        country {
                          objectId
                          name
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      `,
    variables: {
      id: obj4.id
    }
  })).data.country;
  const expectedResult = {
    objectId: obj4.id,
    name: 'imACountry',
    __typename: 'Country',
    companies: [{
      objectId: obj3.id,
      name: 'imACompany',
      __typename: 'Company',
      employees: [{
        objectId: obj1.id,
        name: 'imAnEmployee',
        __typename: 'Employee'
      }],
      teams: [{
        objectId: obj2.id,
        name: 'imATeam',
        __typename: 'Team',
        employees: [{
          objectId: obj1.id,
          name: 'imAnEmployee',
          __typename: 'Employee',
          country: {
            objectId: obj4.id,
            name: 'imACountry',
            __typename: 'Country'
          }
        }]
      }]
    }]
  };
  expect(result).toEqual(expectedResult);
});

it('should respect level permissions', async () => {
  await prepareData();
  await parseGraphQLServer.parseGraphQLSchema.schemaCache.clear();
  async function getObject(className, id, headers) {
    const alias = className.charAt(0).toLowerCase() + className.slice(1);
    const specificQueryResult = await apolloClient.query({
      query: gql`
        query GetSomeObject($id: ID!) {
          get: ${alias}(id: $id) {
            id
            createdAt
            someField
          }
        }
      `,
      variables: {
        id
      },
      context: {
        headers
      }
    });
    return specificQueryResult;
  }
  await Promise.all(objects.slice(0, 3).map(obj => expectAsync(getObject(obj.className, obj.id)).toBeRejectedWith(jasmine.stringMatching('Object not found'))));
  expect((await getObject(object4.className, object4.id)).data.get.someField).toEqual('someValue4');
  await Promise.all(objects.map(async obj => expect((await getObject(obj.className, obj.id, {
    'X-Parse-Master-Key': 'test'
  })).data.get.someField).toEqual(obj.get('someField'))));
  await Promise.all(objects.map(async obj => expect((await getObject(obj.className, obj.id, {
    'X-Parse-Session-Token': user1.getSessionToken()
  })).data.get.someField).toEqual(obj.get('someField'))));
  await Promise.all(objects.map(async obj => expect((await getObject(obj.className, obj.id, {
    'X-Parse-Session-Token': user2.getSessionToken()
  })).data.get.someField).toEqual(obj.get('someField'))));
  await expectAsync(getObject(object2.className, object2.id, {
    'X-Parse-Session-Token': user3.getSessionToken()
  })).toBeRejectedWith(jasmine.stringMatching('Object not found'));
  await Promise.all([object1, object3, object4].map(async obj => expect((await getObject(obj.className, obj.id, {
    'X-Parse-Session-Token': user3.getSessionToken()
  })).data.get.someField).toEqual(obj.get('someField'))));
  await Promise.all(objects.slice(0, 3).map(obj => expectAsync(getObject(obj.className, obj.id, {
    'X-Parse-Session-Token': user4.getSessionToken()
  })).toBeRejectedWith(jasmine.stringMatching('Object not found'))));
  expect((await getObject(object4.className, object4.id, {
    'X-Parse-Session-Token': user4.getSessionToken()
  })).data.get.someField).toEqual('someValue4');
  await Promise.all(objects.slice(0, 2).map(obj => expectAsync(getObject(obj.className, obj.id, {
    'X-Parse-Session-Token': user5.getSessionToken()
  })).toBeRejectedWith(jasmine.stringMatching('Object not found'))));
  expect((await getObject(object3.className, object3.id, {
    'X-Parse-Session-Token': user5.getSessionToken()
  })).data.get.someField).toEqual('someValue3');
  expect((await getObject(object4.className, object4.id, {
    'X-Parse-Session-Token': user5.getSessionToken()
  })).data.get.someField).toEqual('someValue4');
});

