  })).data.find.edges.map(object => object.node.someField)).toEqual(['someValue4']);
  expect((await findObjects('GraphQLClass', {
    'X-Parse-Session-Token': user2.getSessionToken()
  })).data.find.edges.map(object => object.node.someField).sort()).toEqual(['someValue1', 'someValue2', 'someValue3']);
  expect((await findObjects('GraphQLClass', {
