  await parseGraphQLServer.parseGraphQLSchema.schemaCache.clear();
  function deleteObject(className, id, headers) {
    const mutationName = className.charAt(0).toLowerCase() + className.slice(1);
    return apolloClient.mutate({
      mutation: gql`
        mutation DeleteSomeObject(
          $id: ID!
        ) {
          delete: delete${className}(input: { id: $id }) {
            ${mutationName} {
              objectId
            }
          }
        }
      `,
      variables: {
        id
      },
      context: {
        headers
      }
    });
  }
  await Promise.all(objects.slice(0, 3).map(async obj => {
    const originalFieldValue = obj.get('someField');
    await expectAsync(deleteObject(obj.className, obj.id)).toBeRejectedWith(jasmine.stringMatching('Object not found'));
    await obj.fetch({
      useMasterKey: true
    });
    expect(obj.get('someField')).toEqual(originalFieldValue);
  }));
  await Promise.all(objects.slice(0, 3).map(async obj => {
    const originalFieldValue = obj.get('someField');
    await expectAsync(deleteObject(obj.className, obj.id, {
      'X-Parse-Session-Token': user4.getSessionToken()
    })).toBeRejectedWith(jasmine.stringMatching('Object not found'));
    await obj.fetch({
      useMasterKey: true
    });
    expect(obj.get('someField')).toEqual(originalFieldValue);
  }));
  expect((await deleteObject(object4.className, object4.id)).data.delete[object4.className.charAt(0).toLowerCase() + object4.className.slice(1)]).toEqual({
    objectId: object4.id,
    __typename: 'PublicClass'
  });
  await expectAsync(object4.fetch({
    useMasterKey: true
  })).toBeRejectedWith(jasmine.stringMatching('Object not found'));
  expect((await deleteObject(object1.className, object1.id, {
    'X-Parse-Master-Key': 'test'
  })).data.delete[object1.className.charAt(0).toLowerCase() + object1.className.slice(1)]).toEqual({
    objectId: object1.id,
    __typename: 'GraphQLClass'
  });
  await expectAsync(object1.fetch({
    useMasterKey: true
  })).toBeRejectedWith(jasmine.stringMatching('Object not found'));
  expect((await deleteObject(object2.className, object2.id, {
    'X-Parse-Session-Token': user2.getSessionToken()
  })).data.delete[object2.className.charAt(0).toLowerCase() + object2.className.slice(1)]).toEqual({
    objectId: object2.id,
    __typename: 'GraphQLClass'
  });
  await expectAsync(object2.fetch({
    useMasterKey: true
  })).toBeRejectedWith(jasmine.stringMatching('Object not found'));
  expect((await deleteObject(object3.className, object3.id, {
    'X-Parse-Session-Token': user5.getSessionToken()
  })).data.delete[object3.className.charAt(0).toLowerCase() + object3.className.slice(1)]).toEqual({
    objectId: object3.id,
    __typename: 'GraphQLClass'
  });
  await expectAsync(object3.fetch({
    useMasterKey: true
  })).toBeRejectedWith(jasmine.stringMatching('Object not found'));
});

