    expect(getGeoWhere.data.geoWithin.edges[0].node.id).toEqual(createResult.data.createSomeClass.someClass.id);
    expect(getGeoWhere.data.within.edges[0].node.id).toEqual(createResult.data.createSomeClass.someClass.id);
