it('notEqual with array of pointers', done => {
  const children = [];
  const parents = [];
  const promises = [];
  for (let i = 0; i < 2; i++) {
    const proc = iter => {
      const child = new Parse.Object('Child');
      children.push(child);
      const parent = new Parse.Object('Parent');
      parents.push(parent);
      promises.push(child.save().then(() => {
        parents[iter].set('child', [children[iter]]);
        return parents[iter].save();
      }));
    };
    proc(i);
  }
  Promise.all(promises).then(() => {
    const query = new Parse.Query('Parent');
    query.notEqualTo('child', children[0]);
    return query.find();
  }).then(results => {
    expect(results.length).toEqual(1);
    expect(results[0].id).toEqual(parents[1].id);
    done();
  }).catch(error => {
    console.log(error);
  });
});

it('query within dictionary', done => {
  const promises = [];
  for (let i = 0; i < 2; i++) {
    const proc = iter => {
      const obj = new Parse.Object('TestObject');
      obj.set('aDict', {
        x: iter + 1,
        y: iter + 2
      });
      promises.push(obj.save());
    };
    proc(i);
  }
  Promise.all(promises).then(() => {
    const query = new Parse.Query('TestObject');
    query.equalTo('aDict.x', 1);
    return query.find();
  }).then(results => {
    expect(results.length).toEqual(1);
    done();
  }, error => {
    console.log(error);
  });
});

