    user = new Parse.User();
    user.set('username', 'jake');
    user.set('password', 'dog');
    return user.logIn();
  }).then(function (user) {
    equal(user, Parse.User.current());
    equal(user.get('foo'), 'baz');
    const userAgain = new Parse.User();
