    }
  });

  // authenticated
  await logIn(user1);
  const objectAuth = await obj1.fetch();

  // ( {A,B} intersect {B} ) == {B}

  expect(objectAuth.get('testers')).not.toBeDefined('Should not be visible - protected for * and authenticated');
  expect(objectAuth.get('test')).toBeDefined('Should be visible - not protected for everyone (* and authenticated)');
  expect(objectAuth.get('owner')).toBeDefined('Should be visible - not protected for authenticated');
  done();
});

