it('should intersect protected fields when user belongs to multiple roles hierarchy', async done => {
  const admin = await createRole({
    users: user1,
    roleName: 'admin'
  });
  const moder = await createRole({
    users: [user1, user2],
    roleName: 'moder'
  });
  const tester = await createRole({
    roleName: 'tester'
  });

  // admin supersets moder role
  moder.relation('roles').add(admin);
  await moder.save(null, {
    useMasterKey: true
  });
  tester.relation('roles').add(moder);
  await tester.save(null, {
    useMasterKey: true
  });
  const roleAdmin = `role:${admin.get('name')}`;
  const roleModer = `role:${moder.get('name')}`;
  const roleTester = `role:${tester.get('name')}`;
  await updateCLP({
    get: {
      '*': true
    },
    find: {
      '*': true
    },
    protectedFields: {
      [roleAdmin]: [],
      [roleModer]: ['owner'],
      [roleTester]: ['test', 'owner']
    }
  });

  // user1 has admin & moder & tester roles, (moder includes tester).
  await logIn(user1);
  const object = await obj1.fetch();

  // being admin makes all fields visible
  expect(object.get('test')).toBeDefined('Should be visible - admin role explicitly removes protection for all fields ( [] )');
  expect(object.get('owner')).toBeDefined('Should be visible - admin role explicitly removes protection for all fields ( [] )');

  // user2 has moder & tester role, moder includes tester.
  await logIn(user2);
  const objectAgain = await obj1.fetch();

  // being moder allows "test" field
  expect(objectAgain.get('owner')).toBe(undefined, '"owner" should not be visible - protected for each role user belongs to');
  expect(objectAgain.get('test')).toBeDefined('Should be visible - moder role does not protect "test" field');
  done();
});

