it('throws on invalid configuration', async () => {
  const options = [[], 'a', 0, true, {
    enableCheck: 'a'
  }, {
    enableCheck: 0
  }, {
    enableCheck: {}
  }, {
    enableCheck: []
  }, {
    enableCheckLog: 'a'
  }, {
    enableCheckLog: 0
