it('should programmatically reset password', done => {
  const user = new Parse.User();
  const emailAdapter = {
    sendVerificationEmail: () => Promise.resolve(),
    sendPasswordResetEmail: options => {
      request({
        url: options.link,
        followRedirects: false
      }).then(response => {
        expect(response.status).toEqual(302);
        const re = /http:\/\/localhost:8378\/1\/apps\/choose_password\?token=([a-zA-Z0-9]+)\&id=test\&username=zxcv/;
        const match = response.text.match(re);
        if (!match) {
          fail('should have a token');
          done();
          return;
        }
        const token = match[1];
        request({
          url: 'http://localhost:8378/1/apps/test/request_password_reset',
          method: 'POST',
          body: {
            new_password: 'hello',
            token,
            username: 'zxcv'
          },
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded'
          },
          followRedirects: false
        }).then(response => {
          expect(response.status).toEqual(302);
          expect(response.text).toEqual('Found. Redirecting to http://localhost:8378/1/apps/password_reset_success.html?username=zxcv');
          Parse.User.logIn('zxcv', 'hello').then(function () {
            const config = Config.get('test');
            config.database.adapter.find('_User', {
              fields: {}
            }, {
              username: 'zxcv'
            }, {
              limit: 1
            }).then(results => {
              // _perishable_token should be unset after reset password
              expect(results.length).toEqual(1);
              expect(results[0]['_perishable_token']).toEqual(undefined);
              done();
            });
          }, err => {
            jfail(err);
            fail('should login with new password');
            done();
          });
        });
      });
    },
    sendMail: () => {}
  };
  reconfigureServer({
    appName: 'emailing app',
    verifyUserEmails: true,
    emailAdapter: emailAdapter,
    publicServerURL: 'http://localhost:8378/1'
  }).then(() => {
    user.setPassword('asdf');
    user.setUsername('zxcv');
    user.set('email', 'user@parse.com');
    user.signUp().then(() => {
      Parse.User.requestPasswordReset('user@parse.com', {
