      }
    });
  }).then(res => {
    fail(res);
    done();
  }).catch(err => {
    expect(err.status).toBe(404);
    expect(err.text).toMatch(`{"code":${Parse.Error.OBJECT_NOT_FOUND},"error":"Invalid username/password."}`);
    done();
  });
});

