test('children with same names render in correct order', async ({
  equal
}) => {
  const stream = sink();
  const root = pino(stream);
  root.child({
    a: 1
  }).child({
    a: 2
  }).info({
    a: 3
  });
  const {
    a
  } = await once(stream, 'data');
  equal(a, 3, 'last logged object takes precedence');
});

test('use `safe-stable-stringify` to avoid circular dependencies', async ({
  same
}) => {
  const stream = sink();
  const root = pino(stream); // circular depth

  const obj = {};
  obj.a = obj;
  root.info(obj);
  const {
    a
  } = await once(stream, 'data');
  same(a, {
    a: '[Circular]'
  });
});

test('correctly log non circular objects', async ({
  same
}) => {
  const stream = sink();
  const root = pino(stream);
  const obj = {};
  let parent = obj;

  for (let i = 0; i < 10; i++) {
    parent.node = {};
    parent = parent.node;
  }

  root.info(obj);
  const {
    node
  } = await once(stream, 'data');
  same(node, {
    node: {
      node: {
        node: {
          node: {
            node: {
              node: {
                node: {
                  node: {
                    node: {}
                  }
                }
              }
            }
          }
        }
      }
    }
  });
});

test('safe-stable-stringify must be used when interpolating', async t => {
  const stream = sink();
  const instance = pino(stream);
  const o = {
    a: {
      b: {}
    }
  };
  o.a.b.c = o.a.b;
  instance.info('test %j', o);
  const {
    msg
  } = await once(stream, 'data');
  t.equal(msg, 'test {"a":{"b":{"c":"[Circular]"}}}');
});

