  is
}) => {
  const expected = [{
    level: 50,
    msg: 'this is an error'
  }, {
    level: 60,
    msg: 'this is fatal'
  }];
  const instance = pino({
    browser: {
      write(actual) {
        checkLogObjects(is, same, actual, expected.shift());
