  const stream = writeStream(function (data, enc, cb) {
    messageCount += 1;
    cb();
  });
  const streams = [{
    stream: stream
  }, {
    level: 'debug',
    stream: stream
  }, {
    level: 'trace',
    stream: stream
  }, {
    level: 'fatal',
    stream: stream
  }, {
    level: 'silent',
    stream: stream
  }];
