test('dedupe', function (t) {
  let messageCount = 0;
  const stream1 = writeStream(function (data, enc, cb) {
    messageCount -= 1;
    cb();
  });
  const stream2 = writeStream(function (data, enc, cb) {
    messageCount += 1;
    cb();
  });
  const streams = [{
    stream: stream1,
    level: 'info'
  }, {
    stream: stream2,
    level: 'fatal'
  }];
  const log = pino({
    level: 'trace'
  }, multistream(streams, {
    dedupe: true
  }));
  log.info('info stream');
  log.fatal('fatal stream');
  log.fatal('fatal stream');
  t.equal(messageCount, 1);
  t.end();
});

