test('flushSync', function (t) {
  const tmp = file();
  const destination = pino.destination({
    dest: tmp,
    sync: false,
    minLength: 4096
  });
  const stream = multistream([{
    level: 'info',
    stream: destination
  }]);
  const log = pino({
    level: 'info'
  }, stream);
  destination.on('ready', () => {
    log.info('foo');
    log.info('bar');
    stream.flushSync();
    t.equal(readFileSync(tmp, {
      encoding: 'utf-8'
    }).split('\n').length - 1, 2);
    log.info('biz');
    stream.flushSync();
    t.equal(readFileSync(tmp, {
      encoding: 'utf-8'
    }).split('\n').length - 1, 3);
    t.end();
  });
});

test('ends all streams', function (t) {
  t.plan(7);
  const stream = writeStream(function (data, enc, cb) {
    t.pass('message');
    cb();
  });

  stream.flushSync = function () {
    t.pass('flushSync');
  }; // stream2 has no flushSync


  const stream2 = writeStream(function (data, enc, cb) {
    t.pass('message2');
    cb();
  });
  const streams = [{
    stream: stream
  }, {
    level: 'debug',
    stream: stream
  }, {
    level: 'trace',
    stream: stream2
  }, {
    level: 'fatal',
    stream: stream
  }, {
    level: 'silent',
    stream: stream
  }];
  const multi = multistream(streams);
  const log = pino({
    level: 'trace'
  }, multi);
  log.info('info stream');
  multi.end();
});

