test('pino transport options with target', async ({
  teardown,
  same
}) => {
  const destination = file();
  const instance = pino({
    transport: {
      target: 'pino/file',
      options: {
        destination
      }
    }
  });
  const transportStream = instance[pino.symbols.streamSym];
  teardown(transportStream.end.bind(transportStream));
  instance.info('transport option test');
  await watchFileCreated(destination);
  const result = JSON.parse(await readFile(destination));
  delete result.time;
  same(result, {
    pid,
    hostname,
    level: 30,
    msg: 'transport option test'
  });
});

