test('transport options with target and targets', async ({
  fail,
  equal
}) => {
  try {
    pino({
      transport: {
        target: {},
        targets: {}
      }
    });
    fail('must throw');
  } catch (err) {
    equal(err.message, 'only one of target or targets can be specified');
  }
});

test('transport options with target and stream', async ({
  fail,
  equal
}) => {
  try {
    pino({
      transport: {
        target: {}
      }
    }, '/log/null');
    fail('must throw');
  } catch (err) {
    equal(err.message, 'only one of option.transport or stream can be specified');
  }
});

test('transport options with stream', async ({
  fail,
  equal,
  teardown
}) => {
  try {
    const dest1 = file();
    const transportStream = pino.transport({
      target: 'pino/file',
      options: {
        destination: dest1
      }
    });
    teardown(transportStream.end.bind(transportStream));
    pino({
      transport: transportStream
    });
    fail('must throw');
  } catch (err) {
    equal(err.message, 'option.transport do not allow stream, please pass to option directly. e.g. pino(transport)');
  }
});

