it('#734 Invalid value persists in document after failed update', async () => {
    // create a schema
    const schemaEnum = ['A', 'B'];
    const mySchema: RxJsonSchema<{ id: string, children: any[] }> = {
        version: 0,
        primaryKey: 'id',
        required: ['id'],
        type: 'object',
        properties: {
            id: {
                type: 'string',
                maxLength: 100
            },
            children: {
                type: 'array',
                items: {
                    type: 'object',
                    properties: {
                        name: {
                            type: 'string'
                        },
                        abLetter: {
                            type: 'string',
                            enum: schemaEnum,
                        },
                    }
                }
            }
        }
    };

    // generate a random database-name
    const name = randomCouchString(10);

    // create a database
    const db = await createRxDatabase({
        name,
        storage: getRxStoragePouch('memory'),
        ignoreDuplicate: true
    });
    // create a collection
    const colName = randomCouchString(10);
    const collections = await db.addCollections({
        [colName]: {
            schema: mySchema
        }
    });
    const collection = collections[colName];

    // insert a document
    const child1 = {
        name: 'foo',
        abLetter: 'A'
    };
    const child2 = {
        name: 'bar',
        abLetter: 'B'
    };
    const doc = await collection.insert({
        id: randomCouchString(12),
        children: [
            child1,
            child2
        ],
    });

    const colDoc = await collection.findOne({
        selector: {
            id: doc.id
        }
    }).exec();


    try {
        await colDoc.update({
            $set: {
                'children.1.abLetter': 'invalidEnumValue',
            },
        });
    } catch (err) { }

    assert.strictEqual(colDoc.children[1].abLetter, 'B');


    // clean up afterwards
    db.destroy();
});

