      return matchMedia('(color-gamut: srgb)').matches;
    })
  ).toBe(true);
  expect(
    await page.evaluate(() => {
      return matchMedia('(color-gamut: rec2020)').matches;
    })
  ).toBe(false);
  await page.emulateMediaFeatures([{name: 'color-gamut', value: 'p3'}]);
  expect(
    await page.evaluate(() => {
      return matchMedia('(color-gamut: p3)').matches;
    })
  ).toBe(true);
  expect(
    await page.evaluate(() => {
      return matchMedia('(color-gamut: srgb)').matches;
    })
  ).toBe(true);
  expect(
    await page.evaluate(() => {
      return matchMedia('(color-gamut: rec2020)').matches;
    })
  ).toBe(false);
  await page.emulateMediaFeatures([
    {name: 'color-gamut', value: 'rec2020'},
  ]);
  expect(
    await page.evaluate(() => {
      return matchMedia('(color-gamut: p3)').matches;
    })
  ).toBe(true);
  expect(
    await page.evaluate(() => {
      return matchMedia('(color-gamut: srgb)').matches;
    })
  ).toBe(true);
  expect(
    await page.evaluate(() => {
      return matchMedia('(color-gamut: rec2020)').matches;
    })
  ).toBe(true);
});

