it('should report shiftKey', async () => {
  const {page, server} = getTestState();

  await page.goto(server.PREFIX + '/input/keyboard.html');
  const keyboard = page.keyboard;
  const codeForKey = new Map<KeyInput, number>([
    ['Shift', 16],
    ['Alt', 18],
    ['Control', 17],
  ]);
  for (const [modifierKey, modifierCode] of codeForKey) {
    await keyboard.down(modifierKey);
    expect(
      await page.evaluate(() => {
        return (globalThis as any).getResult();
      })
    ).toBe(
      'Keydown: ' +
        modifierKey +
        ' ' +
        modifierKey +
        'Left ' +
        modifierCode +
        ' [' +
        modifierKey +
        ']'
    );
    await keyboard.down('!');
    // Shift+! will generate a keypress
    if (modifierKey === 'Shift') {
      expect(
        await page.evaluate(() => {
          return (globalThis as any).getResult();
        })
      ).toBe(
        'Keydown: ! Digit1 49 [' +
          modifierKey +
          ']\nKeypress: ! Digit1 33 33 [' +
          modifierKey +
          ']'
      );
    } else {
      expect(
        await page.evaluate(() => {
          return (globalThis as any).getResult();
        })
      ).toBe('Keydown: ! Digit1 49 [' + modifierKey + ']');
    }

    await keyboard.up('!');
    expect(
      await page.evaluate(() => {
        return (globalThis as any).getResult();
      })
    ).toBe('Keyup: ! Digit1 49 [' + modifierKey + ']');
    await keyboard.up(modifierKey);
    expect(
      await page.evaluate(() => {
        return (globalThis as any).getResult();
      })
    ).toBe(
      'Keyup: ' +
        modifierKey +
        ' ' +
        modifierKey +
        'Left ' +
        modifierCode +
        ' []'
    );
  }
});

