    }
  }
  const resultBoundingBox = await button.boundingBox();
  expect(resultBoundingBox.x).toBeGreaterThan(150); // padding + margin + border left
  expect(resultBoundingBox.y).toBeGreaterThan(150); // padding + margin + border top
});

