          delete: 1
        }],
        cells: {
          '6:1': {
            content: [{
              insert: 'Hello 6:1!'
            }]
          },
          '52:8': {
            content: [{
              insert: 'Hello 52:8!'
            }]
          }
        }
      }
    }
  }]);
  expect(change1.transform(change2)).toEqual(new Delta([{
    retain: {
      table: {
        rows: [{
          retain: 1
        }, {
          delete: 1
        }],
        cells: {
          '7:1': {
            content: [{
              insert: 'Hello 6:1!'
            }]
          },
          '53:8': {
            content: [{
              insert: 'Hello 52:8!'
            }]
          }
        }
      }
    }
  }]));
});

