it('reverts rows and columns', () => {
  const base = new Delta([{
    insert: {
      table: {
        rows: [{
          insert: {
            id: '11111111'
          }
        }, {
          insert: {
            id: '22222222'
          }
        }],
        columns: [{
          insert: {
            id: '33333333'
          }
        }, {
          insert: {
            id: '44444444'
          },
          attributes: {
            width: 100
          }
        }]
      }
    }
  }]);
  const change = new Delta([{
    retain: {
      table: {
        rows: [{
          remove: {
            id: '22222222'
          }
        }],
        columns: [{
          retain: 1
        }, {
          delete: 1
        }]
      }
    }
  }]);
  expect(change.invert(base)).toEqual(new Delta([{
    retain: {
      table: {
        columns: [{
          retain: 1
        }, {
          insert: {
            id: '44444444'
          },
          attributes: {
            width: 100
          }
        }]
      }
    }
  }]));
});

