    expect(tokens.length).to.eq(2);
    expect(tokens[0]).to.be.an.instanceof(Word);
    expect(tokens[1]).to.be.an.instanceof(Word);

    expect(tokens.map(token => token.value)).to.eql(["prefix", "single token"]);
});

it("doesn't split on a colon", () => {
