    return new Promise(function (res) {
      res([a]);
    });
  };

  var doubleListPromise = function (a) {
    return new Promise(function (res) {
      res(R.concat(a, a));
    });
  };

  var f = composeP([doubleListPromise, toListPromise]);
  return f(1).then(function (res) {
    eq(res, [1, 1]);
  });
});

