  eq(f('Goodbye', 'Cruel World', '!!!'), 'Goodbye, Cruel World!!!');
  eq(f('Hello', 'how are you', '?'), 'Hello, how are you?');
  eq(f('Hello', 'World', '!'), 'Hello, World!');
});

