it('is the inverse of R.apply', function () {
  var a, b, c, d, e, f, g, n;

  var rand = function () {
    return Math.floor(200 * Math.random()) - 100;
  };

  f = Math.max;
  g = R.unapply(R.apply(f));
  n = 1;

  while (n <= 100) {
    a = rand();
    b = rand();
    c = rand();
    d = rand();
    e = rand();
    eq(f(a, b, c, d, e), g(a, b, c, d, e));
    n += 1;
  }

  f = function (xs) {
    return '[' + xs + ']';
  };

  g = R.apply(R.unapply(f));
  n = 1;

  while (n <= 100) {
    a = rand();
    b = rand();
    c = rand();
    d = rand();
    e = rand();
    eq(f([a, b, c, d, e]), g([a, b, c, d, e]));
    n += 1;
  }
});

