it('should disable radios', () => {
  const wrapper = mount(
    <ToggleButtonGroup type="radio" name="items">
      <ToggleButtonGroup.Button id="id1" value={1} disabled>
        Option 1
      </ToggleButtonGroup.Button>
      <ToggleButtonGroup.Button id="id2" value={2} disabled>
        Option 2
      </ToggleButtonGroup.Button>
      <ToggleButtonGroup.Button id="id3" value={3}>
        Option 3
      </ToggleButtonGroup.Button>
    </ToggleButtonGroup>,
  );

  wrapper.find('input[disabled=true]').length.should.equal(2);
  wrapper.find('label.disabled').length.should.equal(2);
  wrapper.assertNone('label[disabled=true]');
});

it('should return an array of values', () => {
  const spy = sinon.spy();
  mount(
    <ToggleButtonGroup type="checkbox" onChange={spy}>
      <ToggleButtonGroup.Button id="id1" value={1}>
        Option 1
      </ToggleButtonGroup.Button>
      <ToggleButtonGroup.Button id="id2" value={2}>
        Option 2
      </ToggleButtonGroup.Button>
      <ToggleButtonGroup.Button id="id3" value={3}>
        Option 3
      </ToggleButtonGroup.Button>
    </ToggleButtonGroup>,
  )
    .find('input[type="checkbox"]')
    .at(1)
    .simulate('change');

  spy.should.have.been.calledWith([2]);
});

it('should return a single value', () => {
  const spy = sinon.spy();
  mount(
    <ToggleButtonGroup type="radio" name="items" onChange={spy}>
      <ToggleButtonGroup.Button id="id1" value={1}>
        Option 1
      </ToggleButtonGroup.Button>
      <ToggleButtonGroup.Button id="id2" value={2}>
        Option 2
      </ToggleButtonGroup.Button>
      <ToggleButtonGroup.Button id="id3" value={3}>
        Option 3
      </ToggleButtonGroup.Button>
    </ToggleButtonGroup>,
  )
    .find('input[type="radio"]')
    .at(1)
    .simulate('change');

  spy.should.have.been.calledWith(2);
});

it('should filter out value when deselected', () => {
  const spy = sinon.spy();
  mount(
    <ToggleButtonGroup
      type="checkbox"
      name="items"
      defaultValue={[1, 2]}
      onChange={spy}
    >
      <ToggleButtonGroup.Button id="id2" value={1}>
        Option 1
      </ToggleButtonGroup.Button>
      <ToggleButtonGroup.Button id="id3" value={2}>
        Option 2
      </ToggleButtonGroup.Button>
    </ToggleButtonGroup>,
  )
    .find('input[type="checkbox"]')
    .at(0)
    .simulate('change');

  spy.should.have.been.calledWith([2]);
});

it('Should output a tooltip with content', () => {
  mount(
    <Tooltip id="test-tooltip" placement="right">
      <strong>Tooltip Content</strong>
    </Tooltip>,
  ).assertSingle(
    '.tooltip[x-placement="right"][role="tooltip"].bs-tooltip-end strong',
  );
});

it('should have offset of [0, 8] for Popovers', () => {
  const ref = React.createRef();

  mount(
    <Wrapper ref={ref}>
      <Popover id="test-popover" />
    </Wrapper>,
  );

  const offset = ref.current.modifiers[0].options.offset();
  expect(offset).to.eql([0, 8]);
});

it('should have offset of [0, 0] for Tooltips', () => {
  const ref = React.createRef();

  mount(
    <Wrapper ref={ref}>
      <Tooltip id="test-tooltip" />
    </Wrapper>,
  );

  const offset = ref.current.modifiers[0].options.offset();
  expect(offset).to.eql([0, 0]);
});

it('should have offset of [0, 0] for any overlay', () => {
  const ref = React.createRef();

  mount(
