test('set-cookie', () => {
  const headers = {}
  const multiValueHeaders = {
    'Set-Cookie': ['rob=TS4Life', 'danny=kittens'],
  }
  const merged = mergeMultiValueHeaders(headers, multiValueHeaders)
  expect(merged).toEqual({
    'set-cookie': ['rob=TS4Life', 'danny=kittens'],
  })
})

test('set-cookie in both headers and multiValueHeaders', () => {
  const headers = { 'set-cookie': 'peter=snaplet' }
  const multiValueHeaders = {
    'set-cookie': ['peter=snaplet', 'tom=hammer'],
  }
  const merged = mergeMultiValueHeaders(headers, multiValueHeaders)
  expect(merged).toEqual({
    'set-cookie': ['peter=snaplet', 'tom=hammer'],
  })
})

test('Checks that the fastify http-proxy plugin is configured correctly', async () => {
  const mockedFastifyInstance = {
    register: jest.fn(),
    get: jest.fn(),
    all: jest.fn(),
    addContentTypeParser: jest.fn(),
    log: console,
  }

  await withApiProxy(mockedFastifyInstance as unknown as FastifyInstance, {
    apiUrl: 'http://localhost',
    apiHost: 'my-api-host',
  })

  const mockedFastifyInstanceOptions =
    mockedFastifyInstance.register.mock.calls[0][1]

  expect(mockedFastifyInstanceOptions).toEqual({
    disableCache: true,
    prefix: 'http://localhost',
    upstream: 'my-api-host',
