it('Does not return cross origin headers if option not specified', async () => {
  const handler = createGraphQLHandler({
    loggerConfig: { logger: createLogger({}), options: {} },
    sdls: {},
    directives: {},
    services: {},
    onException: () => {},
  })

  const mockedEvent = mockLambdaEvent({
