  ['/post/{slug}', [['slug', 'String', '{slug}']]],
  ['/post/{slug...}', [['slug', 'Glob', '{slug...}']]],
  ['/id/{id:Int}', [['id', 'Int', '{id:Int}']]],
])('extracts name and type info', (route, info) => {
