  const multiValueHeaders = {
    'Content-Type': ['application/json', 'text/html'],
  }
  const merged = mergeMultiValueHeaders(headers, multiValueHeaders)
  expect(merged).toEqual({ 'content-type': 'application/json; text/html' })
})

