it("handles empty headers", () => {
  expect(createRemixHeaders({})).toMatchInlineSnapshot(`
    Headers {
      Symbol(map): Object {},
    }
  `);
});

it("handles simple headers", () => {
  expect(createRemixHeaders({ "x-foo": "bar" })).toMatchInlineSnapshot(`
    Headers {
      Symbol(map): Object {
