  expect(res.headers["set-cookie"]).toEqual([
    "first=one; Expires=0; Path=/; HttpOnly; Secure; SameSite=Lax",
    "second=two; MaxAge=1209600; Path=/; HttpOnly; Secure; SameSite=Lax",
