it('should ignore comments files', function (done) {
  rules.load(fixtures.comments.path, function (error, rules) {
    assert.equal(rules.ignore.length, 0, 'zero ignore rules');
    done();
  });
});

it('should allow comments on lines', function (done) {
  rules.load(fixtures.simple.path, function (error, rules) {
    rules.ignore.forEach(function (rule) {
      assert.equal(rule.indexOf('# comment'), -1, 'no comment found');
    });
    done();
  });
});

it('should ignore regular expressions', function (done) {
  rules.load(fixtures.regexp.path, function (error, rules) {
    assert.deepEqual(rules, {
      'watch': [],
      'ignore': []
    }, 'rules are empty');
    done();
  });
});

it('should colour strings', function () {
  var red = colour('red', 'foo');
  assert(red.indexOf('\x1B') !== -1);
});

it('should strip colours strings', function () {
  var red = colour('red', 'foo');
  var plain = colour.strip(red);
  assert(plain.indexOf('\x1B') === -1);
});

it('should disable colour', function () {
  var type = 'fail';
  bus.once('log', function (event) {
    assert.equal(event.message, type);
    assert.ok(event.colour.indexOf(colour[types[type]]) !== -1);
  });
  logger[type](type);
  logger.useColours = false;
  bus.once('log', function (event) {
    assert.equal(event.message, type);
    assert.ok(event.colour.indexOf(colour[types[type]]) === -1);
  });
  logger[type](type);
  logger.useColours = true;
  bus.once('log', function (event) {
    assert.equal(event.message, type);
    assert.ok(event.colour.indexOf(colour[types[type]]) !== -1);
  });
  logger[type](type);
});

it('should be the same with empty targets', function () {
  var result = merge(original, {});
  assert.deepEqual(original, result);
});

it('should merge missing properties', function () {
  var simple = {
    ignore: []
  };
  var result = merge(simple, {
    watch: []
  });
  assert.deepEqual({
    ignore: [],
    watch: []
  }, result);
});

it('should merge complex missing properties', function () {
  var target = [{
    one: 1,
    two: 2
  }, {
    three: 3,
    four: 4
  }];
  var result = merge(original, {
    watch: target
  });
  original.watch = target;
  assert.deepEqual(original, result);
});

it('should merge deep complex missing properties', function () {
  var target = {
    execOptions: {
      verbose: true,
      script: './lib/app',
      args: [],
      exec: 'node',
      execArgs: [],
      ext: ''
    }
  };
  var result = merge(original, target);
  original.execOptions = target.execOptions;
  assert.deepEqual(original, result);
});

it('should ignore existing properties', function () {
  var original = {
    execOptions: {
      ext: 'js'
    }
  };
  var target = {
    execOptions: {
      ext: 'notjs'
    }
  };
  var result = merge(original, target);
  assert.deepEqual(original, result);
  original = {
    ext: 'js'
  };
  target = {
    ext: 'notjs'
  };
  result = merge(original, target);
  assert.deepEqual(original, result);
});

it('should merge in to "empty" properties', function () {
  var target = {
    execOptions: {
      ext: 'js'
    }
  };
  var original = {
    execOptions: {
      ext: ''
    }
  };
  var result = merge(original, target);
  original.execOptions = target.execOptions;
  assert.deepEqual(original, result);
});

it('should merge into empty objects', function () {
  var original = {
    foo: 'bar',
    execOptions: {}
  };
  var target = {
    execOptions: {
      ext: 'js'
    }
  };
  var result = merge(original, target);
  assert.deepEqual({
    foo: 'bar',
    execOptions: {
      ext: 'js'
    }
  }, result);
});

it('should merge into empty arrays', function () {
  var original = {
    foo: 'bar',
    execOptions: []
  };
  var target = {
    execOptions: ['js']
  };
  var result = merge(original, target);
  assert.deepEqual({
    foo: 'bar',
    execOptions: ['js']
  }, result);
});

it('should merge into deep empty arrays', function () {
  // return;
  var original = {
    foo: {
      name: []
    }
  };
  var target = {
    foo: {
      name: ['remy']
    }
  };
  var result = merge(original, target);
  assert.deepEqual({
    foo: {
      name: ['remy']
    }
  }, result);
});

it('should combine the executable and arguments', function () {
  var string = stringify('node', ['./app.js', '--flag']);
  var expected = 'node ./app.js --flag';
  assert(string === expected, "stringified to " + string);
});

it('should not include excess whitespace', function () {
  var string = stringify('node');
  var expected = 'node';
  assert(string === expected, "stringified to " + string);
});

it('should quote arguments with spaces', function () {
  var string = stringify('node', ['./app.js', '--one --two']);
  var expected = 'node ./app.js "--one --two"';
  assert(string === expected, "stringified to " + string);
});

it('should escape quotes', function () {
  var string = stringify('node', ['./app.js', '--one "--two --three"']);
  var expected = 'node ./app.js "--one \\"--two --three\\""';
  assert(string === expected, "stringified to " + string);
});
