it('test custom serializers', function (done) {
  // capture the log record
  var buffer = new StreamRecorder();
  SERVER.once('after', restify.plugins.auditLogger({
    log: pino({
      name: 'audit'
    }, buffer),
    event: 'after',
    serializers: {
      req: function (req) {
        return {
          fooReq: 'barReq'
        };
      },
      res: function (res) {
        return {
          fooRes: 'barRes'
        };
      }
    }
  }));
  SERVER.get('/audit', function aTestHandler(req, res, next) {
    res.send('');
    return next();
  });
  SERVER.on('after', function () {
    var record = buffer.records && buffer.records[0];
    assert.equal(record.req.fooReq, 'barReq');
    assert.equal(record.res.fooRes, 'barRes');
    done();
  });
  CLIENT.get('/audit', function (err, req, res) {
    assert.ifError(err);
  });
});

it('should log handler timers', function (done) {
  // capture the log record
  var buffer = new StreamRecorder();
  var WAIT_IN_MILLISECONDS = 1100;
  SERVER.once('after', restify.plugins.auditLogger({
    log: pino({
      name: 'audit'
    }, buffer),
    event: 'after'
  }));
  SERVER.get('/audit', function aTestHandler(req, res, next) {
    req.startHandlerTimer('audit-sub');
    setTimeout(function () {
      req.endHandlerTimer('audit-sub');
      res.send('');
      return next();
    }, WAIT_IN_MILLISECONDS); // this really should be 1000 but make it 1100 so that the tests
    // don't sporadically fail due to timing issues.
  });
  SERVER.on('after', function () {
    var record = buffer.records && buffer.records[0]; // check timers

    assert.ok(record, 'no log records');
    assert.equal(buffer.records.length, 1, 'should only have 1 log record');
    assertIsAtLeastWithTolerate(record.req.timers.aTestHandler, WAIT_IN_MILLISECONDS * MILLISECOND_IN_MICROSECONDS, TOLERATED_MICROSECONDS, 'atestHandler');
    assertIsAtLeastWithTolerate(record.req.timers['aTestHandler-audit-sub'], WAIT_IN_MILLISECONDS * MILLISECOND_IN_MICROSECONDS, TOLERATED_MICROSECONDS, 'aTestHandler-audit-sub');
    var handlers = Object.keys(record.req.timers);
    assert.equal(handlers[handlers.length - 2], 'aTestHandler-audit-sub', 'sub handler timer not in order');
    assert.equal(handlers[handlers.length - 1], 'aTestHandler', 'aTestHandler not last');
    done();
  });
  CLIENT.get('/audit', function (err, req, res) {
    assert.ifError(err);
  });
});

it('should log anonymous handler timers', function (done) {
  this.timeout(5000); // capture the log record

  var buffer = new StreamRecorder();
  var WAIT_IN_MILLISECONDS = 1000;
  SERVER.once('after', restify.plugins.auditLogger({
    log: pino({
      name: 'audit'
    }, buffer),
    event: 'after'
  }));
  SERVER.pre(function (req, res, next) {
    next();
  });
  SERVER.pre(function (req, res, next) {
    next();
  });
  SERVER.use(function (req, res, next) {
    next();
  });
  SERVER.use(function (req, res, next) {
    next();
  });
  SERVER.get('/audit', function (req, res, next) {
    setTimeout(function () {
      return next();
    }, WAIT_IN_MILLISECONDS);
  }, function (req, res, next) {
    req.startHandlerTimer('audit-sub');
    setTimeout(function () {
      req.endHandlerTimer('audit-sub');
      res.send('');
      return next();
    }, WAIT_IN_MILLISECONDS);
  });
  SERVER.on('after', function () {
    // check timers
    var record = buffer.records && buffer.records[0];
    assert.ok(record, 'no log records');
    assert.equal(buffer.records.length, 1, 'should only have 1 log record');
    assertIsAtLeastWithTolerate(record.req.timers['pre-0'], 0, TOLERATED_MICROSECONDS, 'pre-0');
    assertIsAtLeastWithTolerate(record.req.timers['pre-1'], 0, TOLERATED_MICROSECONDS, 'pre-1');
    assertIsAtLeastWithTolerate(record.req.timers['use-0'], 0, TOLERATED_MICROSECONDS, 'use-0');
    assertIsAtLeastWithTolerate(record.req.timers['use-1'], 0, TOLERATED_MICROSECONDS, 'use-1');
    assertIsAtLeastWithTolerate(record.req.timers['handler-0'], WAIT_IN_MILLISECONDS * MILLISECOND_IN_MICROSECONDS, TOLERATED_MICROSECONDS, 'handler-0');
    assertIsAtLeastWithTolerate(record.req.timers['handler-1'], WAIT_IN_MILLISECONDS * MILLISECOND_IN_MICROSECONDS, TOLERATED_MICROSECONDS, 'handler-1');
    assertIsAtLeastWithTolerate(record.req.timers['handler-1-audit-sub'], WAIT_IN_MILLISECONDS * MILLISECOND_IN_MICROSECONDS, TOLERATED_MICROSECONDS, 'handler-0-audit-sub');
    var handlers = Object.keys(record.req.timers);
    assert.equal(handlers[handlers.length - 2], 'handler-1-audit-sub', 'sub handler timer not in order');
    assert.equal(handlers[handlers.length - 1], 'handler-1', 'handler-1 not last');
    done();
  });
  CLIENT.get('/audit', function (err, req, res) {
    assert.ifError(err);
  });
});

