it('Integration: Should shed load', function (done) {
  var server = restify.createServer();
  var client = {
    close: function () {}
  };
  var opts = {
    interval: 500,
    limit: 0
  };
  plugin = cpuUsageThrottle(opts);
  server.pre(plugin);
  server.get('/foo', function (req, res, next) {
    res.send(200);
    next();
  });
  server.listen(0, '127.0.0.1', function () {
    client = restifyClients.createJsonClient({
      url: 'http://127.0.0.1:' + server.address().port,
      retry: false
    });
    client.get({
      path: '/foo'
    }, function (e, _, res) {
      assert(e, 'Second request is shed');
      assert.equal(res.statusCode, 503, 'Default shed status code returned');
      clearTimeout(plugin._timeout); // we should close the server else mocha wont exit

      server.close();
      done();
    });
  });
});

