it('Integration: Should shed load', function (done) {
  var server = restify.createServer();
  var client = {
    close: function () {}
  };
  var isDone = false;
  var to;

  function finish() {
    if (isDone) {
      return null;
    }

    clearTimeout(to);
    isDone = true;
    client.close();
    server.close();
    return done();
  }

  to = setTimeout(finish, 2000);
  var err = new Error('foo');
  err.statusCode = 555;
  var opts = {
    server: server,
    limit: 1,
    err: err
  };
  server.pre(inflightRequestThrottle(opts));
  var RES;
  server.get('/foo', function (req, res, next) {
    if (RES) {
      res.send(999);
    } else {
      RES = res;
    }
  });
  server.listen(0, '127.0.0.1', function () {
    client = restifyClients.createJsonClient({
      url: 'http://127.0.0.1:' + server.address().port,
      retry: false
    });
    client.get({
      path: '/foo'
    }, function (e, _, res) {
      assert(e === null || e === undefined, 'First request isnt shed');
      assert.equal(res.statusCode, 200, '200 returned on success');
      finish();
    });
    client.get({
      path: '/foo'
    }, function (e, _, res) {
      assert(e, 'Second request is shed');
      assert.equal(e.name, 'InternalServerError', 'Default err returned');
      assert.equal(res.statusCode, 555, 'Default shed status code returned');

      if (RES) {
        RES.send(200);
      }
    });
  });
});

