    return 'pass';
  };
  const prompt = new ctx.Prompt(ctx.fixture, ctx.rl);
  prompt.run().then(answer => {
    expect(answer).to.equal('pass');
    done();
  });
  ctx.rl.emit('line', '');
}));

it('should allow filter function to be asynchronous', () => new Promise(done => {
