it('should expose the Reactive interface', async () => new Promise(done => {
  const prompts = [{
    type: 'input',
    name: 'name1',
    message: 'message',
    default: 'bar'
  }, {
    type: 'input',
    name: 'name',
    message: 'message',
    default: 'doe'
  }];
  const promise = prompt(prompts);
  const spy = sinon.spy();
  promise.ui.process.subscribe(spy, () => {}, () => {
    sinon.assert.calledWith(spy, {
      name: 'name1',
      answer: 'bar'
    });
    sinon.assert.calledWith(spy, {
      name: 'name',
      answer: 'doe'
    });
    done();
  });
  autosubmit(promise.ui);
}));

