it('No exception when using tty other than process.stdin', () => {
  // Manually opens a new tty
  if (ostype === 'Windows_NT' || process.env.GITHUB_ACTIONS) {
    mocha.skip();
  } else {
    const input = new tty.ReadStream(fs.openSync('/dev/tty', 'r+')); // Uses manually opened tty as input instead of process.stdin

    const prompt = inquirer.createPromptModule({
      input,
      skipTTYChecks: false
    });
    const prompts = [{
      type: 'input',
      name: 'q1',
      default: 'foo',
      message: 'message'
    }];
    const promise = prompt(prompts);
    promise.ui.rl.emit('line'); // Release the input tty socket

    input.unref();
    return promise.then(answers => {
      expect(answers).to.deep.equal({
        q1: 'foo'
      });
    });
  }
});

