it('pagination works with multiline choices', function (done) {
  const multilineFixture = {
    message: 'message',
    name: 'name',
    choices: ['a\n\n', 'b\n\n']
  };
  const list = new List(multilineFixture, this.rl);
  const spy = sinon.spy(list.paginator, 'paginate');
  list.run().then(answer => {
    const realIndexPosition1 = spy.firstCall.args[1];
    const realIndexPosition2 = spy.secondCall.args[1]; // 'a\n\n': 0th index, but pagination at 2nd index position due to 2 extra newlines

    expect(realIndexPosition1).to.equal(2); // 'b\n\n': 1st index, but pagination at 5th index position due to 4 extra newlines

    expect(realIndexPosition2).to.equal(5);
    expect(answer).to.equal('b\n\n');
    done();
  });
  this.rl.input.emit('keypress', '', {
    name: 'down'
  });
  this.rl.emit('line');
});

