  }, {
    name: 'Jane Doe'
  }]);
  var result = sifter.search('mith', {
    fields: 'name'
  });
  assert.equal(result.items.length, 1);
  var result = sifter.search('mith', {
    fields: 'name',
    respect_word_boundaries: true
  });
  assert.equal(result.items.length, 0);
  var result = sifter.search('Smi', {
    fields: 'name',
    respect_word_boundaries: true
  });
  assert.equal(result.items.length, 1);
  var result = sifter.search('John Sm', {
    fields: 'name',
    respect_word_boundaries: true
  });
  assert.equal(result.items.length, 1);
  var result = sifter.search('j√ñhn Sm', {
    fields: 'name',
    respect_word_boundaries: true
  });
  assert.equal(result.items.length, 1);
  var result = sifter.search('ohn Smith', {
    fields: 'name',
    respect_word_boundaries: true,
    conjunction: 'and'
  });
  assert.equal(result.items.length, 0);
  var result = sifter.search('√òhn Smith', {
    fields: 'name',
    respect_word_boundaries: true,
    conjunction: 'and'
  });
  assert.equal(result.items.length, 0);
});

